css:
.avatar-collage-section { padding: 5rem 0; text-align: center; position: relative; overflow: hidden; width: 100%; box-sizing: border-box; background-color: var(--color-light, #ffffff); }
.avatar-collage-section * { box-sizing: border-box; }
.avatar-collage-section .container { max-width: 600px; margin: 0 auto; position: relative; z-index: 2; padding: 0 1rem; }
.avatar-collage-section .avatar-collage { position: absolute; inset: 0; z-index: 1; opacity: 0.15; pointer-events: none; }
.avatar-collage-section .avatar-collage img { position: absolute; width: 80px; height: 80px; border-radius: 50%; object-fit: cover; }
.avatar-collage-section blockquote { font-size: 2.25rem; font-weight: 600; line-height: 1.4; margin: 0; color: var(--color-dark, #111827); }
