css:
.testimonial-colorful-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
    font-family: 'Poppins', sans-serif;
    position: relative;
    width: 100%;
    box-sizing: border-box;
}
.testimonial-colorful-section * { box-sizing: border-box; }
.testimonial-colorful-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.testimonial-colorful-section .section-header {
    text-align: center;
    margin-bottom: 5rem;
}
.testimonial-colorful-section .section-header span {
    color: #4CB2A1;
    font-weight: 600;
    letter-spacing: 1px;
    text-transform: uppercase;
    font-size: 0.9rem;
}
.testimonial-colorful-section .section-header h2 {
    font-size: clamp(2rem, 5vw, 2.5rem);
    font-weight: 700;
    color: var(--color-dark, #111827);
    margin-top: 0.5rem;
}
.testimonial-colorful-section .testimonial-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 5rem 1.5rem;
}
.testimonial-colorful-section .testimonial-card {
    position: relative;
    padding: 4rem 1.5rem 1.5rem;
    border-radius: 12px;
    text-align: center;
    margin-top: 35px;
}
.testimonial-colorful-section .testimonial-card img {
    position: absolute;
    top: -35px;
    left: 50%;
    transform: translateX(-50%);
    width: 70px;
    height: 70px;
    border-radius: 50%;
    object-fit: cover;
    border: 4px solid var(--color-light, #FFFFFF);
    box-shadow: 0 4px 10px rgba(0,0,0,0.1);
}
.testimonial-colorful-section .testimonial-card p {
    font-size: 0.95rem;
    color: #374151;
    line-height: 1.6;
    margin-bottom: 1.5rem;
}
.testimonial-colorful-section .testimonial-card cite {
    font-style: normal;
    font-weight: 600;
    color: #111827;
}
