css:
.testimonial-featured-combo-section {
    padding: 5rem 0;
    font-family: 'Inter', sans-serif;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #212529);
    overflow: hidden;
    position: relative;
    width: 100%;
    box-sizing: border-box;
}
.testimonial-featured-combo-section * { box-sizing: border-box; }
.testimonial-featured-combo-section .container {
    width: 90%;
    max-width: 900px;
    margin: 0 auto;
}
.testimonial-featured-combo-section .top-card-wrapper {
    margin-bottom: 60px; /* Creates space for the overlapping avatar */
}
.testimonial-featured-combo-section .top-card {
    background: linear-gradient(135deg, #00C9FF 0%, #92FE9D 100%);
    color: var(--color-dark, #212529);
    padding: 3rem 3rem 0;
    border-radius: 1.5rem;
    text-align: center;
}
.testimonial-featured-combo-section .stars {
    color: #FFD700;
    font-size: 1.5rem;
    text-shadow: 0 1px 2px rgba(0,0,0,0.1);
    margin-bottom: 1rem;
}
.testimonial-featured-combo-section .rating {
    font-size: 1.25rem;
    line-height: 1.6;
    max-width: 600px;
    margin: 0 auto 2rem;
}
.testimonial-featured-combo-section .author {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
    transform: translateY(50%); /* Pulls the author down to overlap */
}
.testimonial-featured-combo-section .author img {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    object-fit: cover;
    border: 4px solid var(--color-light, #FFFFFF);
    box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.testimonial-featured-combo-section .author span {
    font-weight: 600;
    font-size: 1rem;
    color: var(--color-dark, #212529);
    background-color: var(--color-light);
    padding: 0.1rem 0.5rem;
    border-radius: 6px;
    box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}
.testimonial-featured-combo-section .bottom-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    margin-top: 0;
}
@media (min-width: 768px) {
    .testimonial-featured-combo-section .bottom-grid {
        grid-template-columns: 1fr 1fr;
    }
}
.testimonial-featured-combo-section .bottom-card {
    text-align: center;
}
.testimonial-featured-combo-section .icon-wrapper {
    width: 48px;
    height: 48px;
    border-radius: 50%;
    background-color: #4CAF50;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 1.5rem;
}
.testimonial-featured-combo-section .icon-wrapper svg {
    width: 24px;
    height: 24px;
    color: white;
}
.testimonial-featured-combo-section .bottom-card p {
    color: var(--color-secondary, #6B7280);
    font-style: italic;
}
.testimonial-featured-combo-section .bottom-card cite {
    display: block;
    margin-top: 1rem;
    font-style: normal;
    font-weight: 500;
}
