css:
.testimonial-gradient-tabs-section {
    background: linear-gradient(135deg, var(--gradient-start, #0052D4), var(--gradient-end, #04E4DE));
    color: var(--color-light, #FFFFFF);
    padding: 5rem 0;
    font-family: 'Poppins', sans-serif;
    position: relative;
    width: 100%;
    box-sizing: border-box;
}
.testimonial-gradient-tabs-section * { box-sizing: border-box; }
.testimonial-gradient-tabs-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.testimonial-gradient-tabs-section .testimonial-wrapper {
    display: grid;
    grid-template-columns: 1fr;
    gap: 2rem;
    align-items: center;
}
@media (min-width: 768px) {
    .testimonial-gradient-tabs-section .testimonial-wrapper {
        grid-template-columns: 2fr 1fr;
        gap: 4rem;
    }
}
.testimonial-gradient-tabs-section .testimonial-tabs {
    display: flex;
    gap: 1.5rem;
    margin-bottom: 2rem;
    padding-bottom: 1rem;
    border-bottom: 1px solid rgba(255,255,255,0.2);
}
.testimonial-gradient-tabs-section .tab {
    background: none;
    border: none;
    color: var(--color-light, #FFFFFF);
    opacity: 0.7;
    font-family: inherit;
    font-size: 1rem;
    font-weight: 500;
    cursor: pointer;
    padding: 0 0 0.5rem 0;
    position: relative;
}
.testimonial-gradient-tabs-section .tab::after {
    content: '';
    position: absolute;
    bottom: -2px; /* Position below the border-bottom of the container */
    left: 0;
    right: 0;
    height: 3px;
    background-color: var(--color-light, #FFFFFF);
    transform: scaleX(0);
    transition: transform 0.3s ease;
}
.testimonial-gradient-tabs-section .tab.active {
    opacity: 1;
}
.testimonial-gradient-tabs-section .tab.active::after {
    transform: scaleX(1);
}
.testimonial-gradient-tabs-section .testimonial-body {
    position: relative;
    min-height: 200px;
}
.testimonial-gradient-tabs-section .testimonial-quote {
    position: absolute;
    opacity: 0;
    transition: opacity 0.4s ease-in-out;
    pointer-events: none;
}
.testimonial-gradient-tabs-section .testimonial-quote.active {
    position: relative;
    opacity: 1;
    pointer-events: auto;
}
.testimonial-gradient-tabs-section .testimonial-quote p {
    font-size: 1.25rem;
    line-height: 1.7;
    margin-bottom: 1.5rem;
}
.testimonial-gradient-tabs-section .stars {
    color: #FFD700;
    margin-bottom: 0.5rem;
}
.testimonial-gradient-tabs-section .testimonial-quote cite {
    font-style: normal;
    font-weight: 600;
}
.testimonial-gradient-tabs-section .testimonial-image-wrapper {
    position: relative;
    width: 100%;
    max-width: 300px;
    aspect-ratio: 1/1;
    margin: 0 auto;
}
.testimonial-gradient-tabs-section .testimonial-image {
    width: 100%;
    height: 100%;
    border-radius: 1rem;
    object-fit: cover;
    box-shadow: 0 10px 30px rgba(0,0,0,0.2);
    position: absolute;
    opacity: 0;
    transition: opacity 0.4s ease-in-out;
}
.testimonial-gradient-tabs-section .testimonial-image.active {
    opacity: 1;
}
