css:
.magazine-pullquote-section { padding: 5rem 0; background: #fff; font-family: 'Lora', serif; position: relative; width: 100%; box-sizing: border-box; }
.magazine-pullquote-section * { box-sizing: border-box; }
.magazine-pullquote-section .container { max-width: 700px; margin: 0 auto; }
.magazine-pullquote-section .pull-quote { border: none; text-align: center; margin: 0; }
.magazine-pullquote-section .pull-quote p { font-size: 2.25rem; font-weight: 600; line-height: 1.4; color: #111; position: relative; padding: 0 3rem; }
.magazine-pullquote-section .pull-quote p::before, .magazine-pullquote-section .pull-quote p::after { content: '“'; font-family: Georgia, serif; font-size: 6rem; position: absolute; opacity: 0.1; line-height: 0; }
.magazine-pullquote-section .pull-quote p::before { top: 0.5em; left: 0; content: '“'; }
.magazine-pullquote-section .pull-quote p::after { content: '”'; bottom: -0.2em; right: 0; }
.magazine-pullquote-section .pull-quote cite { display: flex; align-items: center; justify-content: center; margin-top: 2rem; font-style: normal; font-family: 'Inter', sans-serif; }
.magazine-pullquote-section .pull-quote cite img { width: 50px; height: 50px; border-radius: 50%; margin-right: 1rem; }
