css:
.star-tiles-section { padding: 4rem 0; position: relative; width: 100%; box-sizing: border-box; }
.star-tiles-section * { box-sizing: border-box; }
.star-tiles-section .container { display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr)); gap: 1.5rem; max-width: 1000px; margin: 0 auto; padding: 0 1rem; }
.star-tiles-section .tile { background: #f9fafb; padding: 1.5rem; border-radius: 0.75rem; border: 1px solid #e5e7eb; }
.star-tiles-section .stars { color: #f59e0b; margin-bottom: 0.5rem; }
.star-tiles-section .tile p { font-style: italic; margin-bottom: 1rem; color: var(--color-dark, #111827); }
.star-tiles-section .tile cite { font-style: normal; font-weight: 600; font-size: 0.9rem; color: var(--color-secondary, #6b7280); }
