css:
.section-testimonial-tabs-modern-a1b2 {
    --color-primary: #4CAF50;
    --color-secondary: #8BC34A;
    --color-dark: #212121;
    --color-light: #ffffff;
    --color-surface-1: #f5f5f5;
    --color-border: #e0e0e0;
    --font-heading: 'Montserrat', sans-serif;
    --font-body: 'Open Sans', sans-serif;
    font-family: var(--font-body);
    padding: 60px 0;
    background-color: var(--color-light);
}

.section-testimonial-tabs-modern-a1b2 .container {
    max-width: 1100px;
    margin: 0 auto;
    padding: 0 20px;
}

.section-testimonial-tabs-modern-a1b2 .testimonial-tabs-wrapper {
    text-align: center;
}

.section-testimonial-tabs-modern-a1b2 .tab-buttons {
    display: flex;
    justify-content: center;
    gap: 15px;
    margin-bottom: 30px;
    flex-wrap: wrap;
}

.section-testimonial-tabs-modern-a1b2 .tab-button {
    padding: 10px 20px;
    border: none;
    background-color: var(--color-surface-1);
    color: var(--color-dark);
    cursor: pointer;
    border-radius: 5px;
    font-size: 16px;
    font-weight: 600;
    transition: background-color 0.3s, color 0.3s;
}

.section-testimonial-tabs-modern-a1b2 .tab-button.active {
    background-color: var(--color-primary);
    color: var(--color-light);
}

.section-testimonial-tabs-modern-a1b2 .tab-content {
    display: none;
    text-align: center;
    animation: fadeIn 0.5s ease;
}

.section-testimonial-tabs-modern-a1b2 .tab-content.active {
    display: block;
}

.section-testimonial-tabs-modern-a1b2 .testimonial-card {
    background-color: var(--color-light);
    padding: 30px;
    border-radius: 8px;
    box-shadow: 0 4px 15px rgba(0, 0, 0, 0.1);
    max-width: 600px;
    margin: 0 auto;
    display: flex;
    flex-direction: column;
    align-items: center;
}

.section-testimonial-tabs-modern-a1b2 .testimonial-avatar {
    width: 100px;
    height: 100px;
    border-radius: 50%;
    object-fit: cover;
    margin-bottom: 20px;
    border: 3px solid var(--color-primary);
}

.section-testimonial-tabs-modern-a1b2 .testimonial-quote {
    font-size: 1.1em;
    line-height: 1.6;
    color: var(--color-dark);
    margin-bottom: 20px;
    font-style: italic;
}

.section-testimonial-tabs-modern-a1b2 .testimonial-name {
    font-size: 1.2em;
    font-family: var(--font-heading);
    color: var(--color-primary);
    margin-bottom: 5px;
}

.section-testimonial-tabs-modern-a1b2 .testimonial-title {
    font-size: 0.9em;
    color: var(--color-secondary);
    opacity: 0.9;
}

@media (max-width: 768px) {
    .section-testimonial-tabs-modern-a1b2 .tab-buttons {
        flex-direction: column;
        gap: 10px;
    }
    .section-testimonial-tabs-modern-a1b2 .tab-button {
        width: 100%;
    }
    .section-testimonial-tabs-modern-a1b2 .testimonial-card {
        padding: 20px;
    }
    .section-testimonial-tabs-modern-a1b2 .testimonial-avatar {
        width: 80px;
        height: 80px;
    }
}
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}
