css:
.section-testimonials-bento-grid-beta {
  padding: 5rem 0;
  background-color: var(--color-light);
}
.section-testimonials-bento-grid-beta .container {
  max-width: 1000px;
  margin: 0 auto;
  padding: 0 1.5rem;
}
.section-testimonials-bento-grid-beta .section-title {
  text-align: center;
  font-family: var(--font-heading);
  margin-bottom: 3rem;
  font-size: 2.5rem;
  color: var(--color-dark);
}
.section-testimonials-bento-grid-beta .bento-grid {
  display: grid;
  grid-template-columns: 1fr;
  gap: 1.5rem;
}
@media (min-width: 768px) {
  .section-testimonials-bento-grid-beta .bento-grid {
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: auto auto;
  }
  .section-testimonials-bento-grid-beta .large {
    grid-column: span 2;
    grid-row: span 2;
  }
  .section-testimonials-bento-grid-beta .wide {
    grid-column: span 3;
  }
}
.section-testimonials-bento-grid-beta .card {
  background: var(--color-surface-1);
  padding: 2rem;
  border-radius: 1.5rem;
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  box-shadow: 0 4px 6px rgba(0,0,0,0.02);
  transition: transform 0.2s;
}
.section-testimonials-bento-grid-beta .card:hover {
  transform: scale(1.01);
}
.section-testimonials-bento-grid-beta .card.alt-color {
  background-color: var(--color-primary);
  color: var(--color-dark);
}
.section-testimonials-bento-grid-beta .card h3 {
  font-size: 1.5rem;
  margin-bottom: 1rem;
  line-height: 1.3;
  color: var(--color-dark);
}
.section-testimonials-bento-grid-beta .card p {
  font-size: 1rem;
  color: var(--color-dark);
  opacity: 0.8;
  margin-bottom: 1.5rem;
}
.section-testimonials-bento-grid-beta .user {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-top: auto;
}
.section-testimonials-bento-grid-beta .user img {
  width: 40px;
  height: 40px;
  border-radius: 50%;
  object-fit: cover;
}
.section-testimonials-bento-grid-beta .user span {
  font-weight: 600;
  font-size: 0.9rem;
  color: var(--color-dark);
}
.section-testimonials-bento-grid-beta .flex-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  width: 100%;
}
.section-testimonials-bento-grid-beta .rating-badge {
  background: var(--color-dark);
  color: var(--color-light);
  padding: 0.5rem 1rem;
  border-radius: 1rem;
  font-weight: bold;
}

js:
(() => {
  const section = document.querySelector('.section-testimonials-bento-grid-beta');
  if (!section) return;
  const imgs = section.querySelectorAll('img');
  imgs.forEach(img => {
    if (img.src.startsWith('https://pixabay.com/api/')) {
      fetch(img.src).then(r => r.json()).then(data => {
        if (data.hits && data.hits.length > 0) img.src = data.hits[0].webformatURL;
      }).catch(console.error);
    }
  });
})();
