css:
.section-testimonials-dark-mode-delta {
  background-color: var(--color-dark);
  color: var(--color-light);
  padding: 6rem 0;
}
.section-testimonials-dark-mode-delta .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1.5rem;
}
.section-testimonials-dark-mode-delta .intro {
  text-align: center;
  margin-bottom: 4rem;
}
.section-testimonials-dark-mode-delta .heading {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  color: var(--color-light);
  margin-bottom: 1rem;
}
.section-testimonials-dark-mode-delta .subheading {
  color: rgba(255,255,255,0.7);
  font-size: 1.1rem;
}
.section-testimonials-dark-mode-delta .cards-row {
  display: grid;
  grid-template-columns: 1fr;
  gap: 2rem;
}
@media (min-width: 992px) {
  .section-testimonials-dark-mode-delta .cards-row {
    grid-template-columns: repeat(3, 1fr);
  }
}
.section-testimonials-dark-mode-delta .dark-card {
  background: rgba(255,255,255,0.05);
  border: 1px solid rgba(255,255,255,0.1);
  border-radius: 1rem;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  transition: all 0.3s ease;
}
.section-testimonials-dark-mode-delta .dark-card:hover {
  background: rgba(255,255,255,0.1);
  transform: translateY(-5px);
}
.section-testimonials-dark-mode-delta .dark-card.highlight {
  border-color: var(--color-primary);
  box-shadow: 0 0 20px rgba(153, 226, 0, 0.1);
}
.section-testimonials-dark-mode-delta .card-body p {
  font-size: 1.1rem;
  line-height: 1.6;
  color: rgba(255,255,255,0.9);
  margin-bottom: 2rem;
  flex-grow: 1;
}
.section-testimonials-dark-mode-delta .card-footer {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-top: auto;
}
.section-testimonials-dark-mode-delta .card-footer img {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  object-fit: cover;
  border: 2px solid var(--color-primary);
}
.section-testimonials-dark-mode-delta .meta strong {
  display: block;
  color: var(--color-light);
  font-size: 1rem;
}
.section-testimonials-dark-mode-delta .meta span {
  display: block;
  color: rgba(255,255,255,0.5);
  font-size: 0.85rem;
}

js:
(() => {
  const section = document.querySelector('.section-testimonials-dark-mode-delta');
  if (!section) return;
  const imgs = section.querySelectorAll('img');
  imgs.forEach(img => {
    if (img.src.startsWith('https://pixabay.com/api/')) {
      fetch(img.src).then(r => r.json()).then(data => {
        if (data.hits && data.hits.length > 0) img.src = data.hits[0].webformatURL;
      }).catch(console.error);
    }
  });
})();
