css:
:root {
  --color-primary: #7367f0;
  --color-secondary: #a8aaae;
  --color-surface-1: #f8f9fa;
  --color-dark: #333333;
  --color-light: #ffffff;
  --color-border: #e1e4e8;
  --font-heading: 'Inter', sans-serif;
  --font-body: 'Inter', sans-serif;
}

.section-testimonials-masonry-v1 {
  background-color: var(--color-light);
  padding: 4rem 1rem;
  font-family: var(--font-body);
}

.section-testimonials-masonry-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-testimonials-masonry-v1 .header-group {
  text-align: center;
  margin-bottom: 3rem;
}

.section-testimonials-masonry-v1 .section-title {
  font-family: var(--font-heading);
  font-size: 2.5rem;
  color: var(--color-dark);
  margin-bottom: 0.5rem;
}

.section-testimonials-masonry-v1 .section-subtitle {
  color: var(--color-secondary);
  font-size: 1.1rem;
}

.section-testimonials-masonry-v1 .masonry-grid {
  column-count: 1;
  column-gap: 1.5rem;
}

@media (min-width: 768px) {
  .section-testimonials-masonry-v1 .masonry-grid {
    column-count: 2;
  }
}

@media (min-width: 1024px) {
  .section-testimonials-masonry-v1 .masonry-grid {
    column-count: 3;
  }
}

.section-testimonials-masonry-v1 .testimonial-card {
  background-color: var(--color-surface-1);
  border-radius: 12px;
  padding: 1.5rem;
  margin-bottom: 1.5rem;
  break-inside: avoid;
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.05);
  transition: transform 0.2s ease;
}

.section-testimonials-masonry-v1 .testimonial-card:hover {
  transform: translateY(-4px);
}

.section-testimonials-masonry-v1 .card-header {
  display: flex;
  align-items: center;
  margin-bottom: 1rem;
}

.section-testimonials-masonry-v1 .user-avatar {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  object-fit: cover;
  margin-right: 1rem;
  border: 2px solid var(--color-light);
  box-shadow: 0 0 0 2px var(--color-primary);
}

.section-testimonials-masonry-v1 .user-info h4 {
  margin: 0;
  font-size: 0.95rem;
  font-weight: 600;
  color: var(--color-dark);
}

.section-testimonials-masonry-v1 .badge-verified {
  display: inline-block;
  background-color: rgba(115, 103, 240, 0.12);
  color: var(--color-primary);
  font-size: 0.7rem;
  padding: 2px 8px;
  border-radius: 12px;
  font-weight: 600;
  margin-top: 2px;
  text-transform: uppercase;
}

.section-testimonials-masonry-v1 .rating {
  color: #ff9f43;
  margin-bottom: 0.75rem;
  font-size: 0.9rem;
  letter-spacing: 2px;
}

.section-testimonials-masonry-v1 .review-text {
  color: #5e5873;
  font-size: 0.95rem;
  line-height: 1.6;
  margin: 0;
}

js:
(() => {
  const section = document.querySelector('.section-testimonials-masonry-v1');
  if (!section) return;

  // Pixabay Image Fetcher
  const images = section.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  images.forEach(img => {
    fetch(img.src)
      .then(res => res.json())
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].webformatURL;
        } else {
          // Fallback placeholder if no hits
          img.src = 'https://via.placeholder.com/150';
        }
      })
      .catch(err => console.error('Pixabay API Error:', err));
  });
})();
