css:
.section-testimonials-minimal-list-lambda {
  padding: 5rem 0;
  background-color: var(--color-surface-1);
}
.section-testimonials-minimal-list-lambda .container {
  max-width: 900px;
  margin: 0 auto;
  padding: 0 1.5rem;
}
.section-testimonials-minimal-list-lambda h2 {
  font-family: var(--font-heading);
  font-size: 2rem;
  margin-bottom: 3rem;
  color: var(--color-dark);
  border-bottom: 2px solid var(--color-light);
  padding-bottom: 1rem;
}
.section-testimonials-minimal-list-lambda .review-row {
  display: flex;
  flex-direction: column;
  padding: 2rem 0;
  border-bottom: 1px solid var(--color-light);
}
@media (min-width: 768px) {
  .section-testimonials-minimal-list-lambda .review-row {
    flex-direction: row;
    align-items: flex-start;
    gap: 2rem;
  }
}
.section-testimonials-minimal-list-lambda .user-info {
  display: flex;
  align-items: center;
  gap: 1rem;
  min-width: 200px;
  margin-bottom: 1rem;
}
.section-testimonials-minimal-list-lambda .user-info img {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
}
.section-testimonials-minimal-list-lambda .user-info h4 {
  font-size: 1rem;
  margin: 0;
  color: var(--color-dark);
}
.section-testimonials-minimal-list-lambda .user-info span {
  font-size: 0.85rem;
  color: var(--color-secondary);
}
.section-testimonials-minimal-list-lambda .review-text {
  flex: 1;
}
.section-testimonials-minimal-list-lambda .stars {
  color: var(--color-primary);
  margin-bottom: 0.5rem;
  font-size: 1rem;
}
.section-testimonials-minimal-list-lambda .review-text p {
  font-size: 1.05rem;
  line-height: 1.6;
  color: var(--color-dark);
  opacity: 0.8;
}

js:
(() => {
  const section = document.querySelector('.section-testimonials-minimal-list-lambda');
  if (!section) return;
  const imgs = section.querySelectorAll('img');
  imgs.forEach(img => {
    if (img.src.startsWith('https://pixabay.com/api/')) {
      fetch(img.src).then(r => r.json()).then(data => {
        if (data.hits && data.hits.length > 0) img.src = data.hits[0].webformatURL;
      }).catch(console.error);
    }
  });
})();
