css:
.section-testimonials-sidebar-tabs-theta {
  padding: 5rem 0;
  background-color: var(--color-light);
}
.section-testimonials-sidebar-tabs-theta .container {
  max-width: 1000px;
  margin: 0 auto;
  padding: 0 1.5rem;
}
.section-testimonials-sidebar-tabs-theta .tabs-wrapper {
  display: flex;
  flex-direction: column;
  background: var(--color-surface-1);
  border-radius: 1rem;
  overflow: hidden;
  box-shadow: 0 10px 40px rgba(0,0,0,0.05);
}
@media (min-width: 768px) {
  .section-testimonials-sidebar-tabs-theta .tabs-wrapper {
    flex-direction: row;
    min-height: 400px;
  }
}
.section-testimonials-sidebar-tabs-theta .tab-list {
  flex: 1;
  background: #f9f9f9;
  border-right: 1px solid #eee;
  display: flex;
  flex-direction: column;
}
.section-testimonials-sidebar-tabs-theta .tab-btn {
  padding: 1.5rem;
  text-align: left;
  background: transparent;
  border: none;
  border-bottom: 1px solid #eee;
  cursor: pointer;
  font-weight: 600;
  color: var(--color-dark);
  opacity: 0.6;
  transition: all 0.2s;
}
.section-testimonials-sidebar-tabs-theta .tab-btn:hover {
  background: rgba(0,0,0,0.02);
  opacity: 1;
}
.section-testimonials-sidebar-tabs-theta .tab-btn.active {
  background: #fff;
  opacity: 1;
  border-left: 4px solid var(--color-primary);
  color: var(--color-primary);
}
.section-testimonials-sidebar-tabs-theta .tab-content {
  flex: 2;
  padding: 3rem;
  position: relative;
}
.section-testimonials-sidebar-tabs-theta .content-item {
  display: none;
  animation: fadeIn 0.5s ease;
}
.section-testimonials-sidebar-tabs-theta .content-item.active {
  display: block;
}
@keyframes fadeIn {
  from { opacity: 0; transform: translateY(10px); }
  to { opacity: 1; transform: translateY(0); }
}
.section-testimonials-sidebar-tabs-theta .content-item h3 {
  font-family: var(--font-heading);
  font-size: 1.5rem;
  color: var(--color-dark);
  margin-bottom: 1.5rem;
}
.section-testimonials-sidebar-tabs-theta .content-item p {
  font-size: 1.1rem;
  line-height: 1.6;
  color: var(--color-secondary);
  margin-bottom: 2rem;
}
.section-testimonials-sidebar-tabs-theta .author {
  display: flex;
  align-items: center;
  gap: 1rem;
}
.section-testimonials-sidebar-tabs-theta .author img {
  width: 50px;
  height: 50px;
  border-radius: 50%;
  object-fit: cover;
}
