css:
.section-testimonials-single-spotlight-v3 {
  padding: 6rem 0;
  background-color: var(--color-surface-1);
}
.section-testimonials-single-spotlight-v3 .container {
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 1.5rem;
}
.section-testimonials-single-spotlight-v3 .spotlight-wrapper {
  display: flex;
  flex-direction: column;
  gap: 3rem;
  align-items: center;
}
@media (min-width: 992px) {
  .section-testimonials-single-spotlight-v3 .spotlight-wrapper {
    flex-direction: row;
    align-items: center;
  }
}
.section-testimonials-single-spotlight-v3 .image-side {
  flex: 1;
  position: relative;
}
.section-testimonials-single-spotlight-v3 .image-side img {
  width: 100%;
  max-width: 450px;
  height: auto;
  border-radius: 2rem;
  box-shadow: 20px 20px 0 var(--color-primary);
}
.section-testimonials-single-spotlight-v3 .text-side {
  flex: 1.2;
  padding: 1rem;
}
.section-testimonials-single-spotlight-v3 .icon {
  font-size: 4rem;
  color: var(--color-primary);
  line-height: 1;
  margin-bottom: 1rem;
  opacity: 0.5;
}
.section-testimonials-single-spotlight-v3 blockquote {
  font-family: var(--font-heading);
  font-size: clamp(1.5rem, 3vw, 2rem);
  color: var(--color-dark);
  line-height: 1.3;
  margin: 0 0 2rem 0;
  font-weight: 600;
}
.section-testimonials-single-spotlight-v3 .author-block h3 {
  font-size: 1.25rem;
  color: var(--color-dark);
  margin-bottom: 0.25rem;
}
.section-testimonials-single-spotlight-v3 .author-block p {
  color: var(--color-secondary);
  font-size: 1rem;
  margin-bottom: 2rem;
}
.section-testimonials-single-spotlight-v3 .logos {
  display: flex;
  gap: 1.5rem;
  opacity: 0.4;
  font-weight: bold;
  text-transform: uppercase;
  letter-spacing: 1px;
  color: var(--color-dark);
}

js:
(() => {
  const section = document.querySelector('.section-testimonials-single-spotlight-v3');
  if (!section) return;
  const imgs = section.querySelectorAll('img');
  imgs.forEach(img => {
    if (img.src.startsWith('https://pixabay.com/api/')) {
      fetch(img.src).then(r => r.json()).then(data => {
        if (data.hits && data.hits.length > 0) img.src = data.hits[0].webformatURL;
      }).catch(console.error);
    }
  });
})();
