css:
.section-testimonials-split-grid-alpha {
  padding: 5rem 0;
  background-color: var(--color-light);
  font-family: var(--font-body);
}
.section-testimonials-split-grid-alpha .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1.5rem;
}
.section-testimonials-split-grid-alpha .top-split {
  display: flex;
  flex-direction: column;
  gap: 3rem;
  margin-bottom: 4rem;
  align-items: center;
}
@media (min-width: 992px) {
  .section-testimonials-split-grid-alpha .top-split {
    flex-direction: row;
    align-items: center;
    justify-content: space-between;
  }
}
.section-testimonials-split-grid-alpha .text-content {
  flex: 1;
  max-width: 600px;
}
.section-testimonials-split-grid-alpha .label {
  text-transform: uppercase;
  font-size: 0.875rem;
  letter-spacing: 1px;
  font-weight: 600;
  color: var(--color-dark);
  display: block;
  margin-bottom: 1rem;
}
.section-testimonials-split-grid-alpha .heading {
  font-family: var(--font-heading);
  font-size: clamp(2rem, 4vw, 3rem);
  color: var(--color-dark);
  line-height: 1.1;
  margin-bottom: 1.5rem;
  font-weight: 700;
}
.section-testimonials-split-grid-alpha .description {
  color: var(--color-dark);
  opacity: 0.8;
  font-size: 1.125rem;
  line-height: 1.6;
  margin-bottom: 2rem;
}
.section-testimonials-split-grid-alpha .btn-primary {
  background-color: var(--color-primary);
  color: var(--color-dark);
  border: none;
  padding: 1rem 2rem;
  font-weight: 600;
  border-radius: 2rem;
  cursor: pointer;
  transition: transform 0.2s;
}
.section-testimonials-split-grid-alpha .btn-primary:hover {
  transform: translateY(-2px);
  opacity: 0.9;
}
.section-testimonials-split-grid-alpha .image-content {
  flex: 1;
  width: 100%;
}
.section-testimonials-split-grid-alpha .hero-img {
  width: 100%;
  height: 350px;
  object-fit: cover;
  border-radius: 1rem;
  box-shadow: 0 10px 30px rgba(0,0,0,0.1);
}
.section-testimonials-split-grid-alpha .grid-content {
  display: grid;
  grid-template-columns: 1fr;
  gap: 2rem;
}
@media (min-width: 768px) {
  .section-testimonials-split-grid-alpha .grid-content {
    grid-template-columns: repeat(3, 1fr);
  }
}
.section-testimonials-split-grid-alpha .review-card {
  background: var(--color-surface-1);
  padding: 2rem;
  border-radius: 1rem;
  box-shadow: 0 4px 20px rgba(0,0,0,0.05);
  transition: transform 0.3s ease;
  display: flex;
  flex-direction: column;
}
.section-testimonials-split-grid-alpha .review-card:hover {
  transform: translateY(-5px);
}
.section-testimonials-split-grid-alpha .card-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 1rem;
}
.section-testimonials-split-grid-alpha .quote-icon {
  font-size: 2rem;
  color: var(--color-primary);
  line-height: 1;
}
.section-testimonials-split-grid-alpha .rating {
  color: #f59e0b;
  font-size: 0.9rem;
}
.section-testimonials-split-grid-alpha .card-title {
  font-family: var(--font-heading);
  font-size: 1.25rem;
  color: var(--color-dark);
  margin-bottom: 0.75rem;
}
.section-testimonials-split-grid-alpha .card-text {
  font-size: 1rem;
  color: var(--color-dark);
  opacity: 0.7;
  line-height: 1.6;
  margin-bottom: 1.5rem;
  flex-grow: 1;
}
.section-testimonials-split-grid-alpha .author {
  display: flex;
  align-items: center;
  gap: 1rem;
}
.section-testimonials-split-grid-alpha .author img {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  object-fit: cover;
}
.section-testimonials-split-grid-alpha .author strong {
  display: block;
  font-size: 0.95rem;
  color: var(--color-dark);
}
.section-testimonials-split-grid-alpha .author span {
  display: block;
  font-size: 0.85rem;
  color: var(--color-dark);
  opacity: 0.6;
}

js:
(() => {
  const section = document.querySelector('.section-testimonials-split-grid-alpha');
  if (!section) return;
  const imgs = section.querySelectorAll('img');
  imgs.forEach(img => {
    if (img.src.startsWith('https://pixabay.com/api/')) {
      fetch(img.src)
        .then(r => r.json())
        .then(data => {
          if (data.hits && data.hits.length > 0) {
            img.src = data.hits[0].webformatURL;
          } else {
            img.style.backgroundColor = '#e0e0e0';
          }
        })
        .catch(e => console.error('Image fetch error:', e));
    }
  });
})();
