css:
.text-stacked-cards-section {
  font-family: 'Asul', sans-serif;
  font-size: 22px;
  overflow: hidden; 
  width: 100%;
  position: relative;
  background: linear-gradient(to right, rgba(233,223,196,1) 0%,rgba(233,223,196,1) 1%,rgba(237,227,200,1) 2%,rgba(237,227,200,1) 24%,rgba(235,221,195,1) 25%,rgba(233,223,196,1) 48%,rgba(235,221,195,1) 49%,rgba(230,216,189,1) 52%,rgba(230,216,189,1) 53%,rgba(233,219,192,1) 54%,rgba(230,216,189,1) 55%,rgba(230,216,189,1) 56%,rgba(233,219,192,1) 57%,rgba(230,216,189,1) 58%,rgba(230,216,189,1) 73%,rgba(233,219,192,1) 74%,rgba(233,219,192,1) 98%,rgba(235,221,195,1) 100%);
  background-size: 120px;
  background-repeat: repeat;
  padding: 4rem 0;
  min-height: 35em;
}
.text-stacked-cards-section img { max-width: 100%; }
.text-stacked-cards-section mark { background-color: #FFFF00; border: 1px dashed #FFFF00; }
.text-stacked-cards-section .all { width: 21em; position: relative; margin: 0 auto; max-width: calc(100% - 6em); height: 25em; }
.text-stacked-cards-section .all > section article { overflow: auto; margin: 0; opacity: 0; transition: 1s opacity ease-in-out; position: relative; }
.text-stacked-cards-section .all > section article > *:not(img) { padding-right: 1.5em; padding-left: 2.5em; }
.text-stacked-cards-section .all > section h1 { margin-top: 0; }
.text-stacked-cards-section .all > section { cursor: pointer; user-select: none; position: absolute; top: 0; height: 20em; max-height: calc(100vh - 10em); background-color: white; border: 1px solid black; padding: 3.5em 0 1.5em; overflow: auto; transition: 1s transform ease-in-out, 1s opacity ease-in-out; background-color: #fafafa; }
.text-stacked-cards-section .all > section:before { display: block; font-size: 0.65em; min-width: 1em; text-align: center; position: absolute; top: 2em; right: 1.5em; background-color: rgba(80, 11, 6, 0.4); color: white; padding: 0.3em; }
.text-stacked-cards-section .all > section p:first-of-type { margin-top: 0; }
.text-stacked-cards-section .all > section:nth-child(1) { z-index: 10; transform: rotate(-3deg); }
.text-stacked-cards-section .all > section:nth-child(1):before { content: '1'; }
.text-stacked-cards-section .all > section:nth-child(1).leave { transform: rotate(-3deg) translateY(50em) scale3d(0.6, 0.6, 1); opacity: 0.3; }

.text-stacked-cards-section .all > section:nth-child(2) { z-index: 9; transform: rotate(2deg); }
.text-stacked-cards-section .all > section:nth-child(2):before { content: '2'; }
.text-stacked-cards-section .all > section:nth-child(2).leave { transform: rotate(2deg) translateY(50em) scale3d(0.6, 0.6, 1); opacity: 0.3; }

.text-stacked-cards-section .all > section:nth-child(3) { z-index: 8; transform: rotate(-1deg); }
.text-stacked-cards-section .all > section:nth-child(3):before { content: '3'; }
.text-stacked-cards-section .all > section:nth-child(3).leave { transform: rotate(-1deg) translateY(50em) scale3d(0.6, 0.6, 1); opacity: 0.3; }

.text-stacked-cards-section .all > section:nth-child(4) { z-index: 7; transform: rotate(3deg); }
.text-stacked-cards-section .all > section:nth-child(4):before { content: '4'; }
.text-stacked-cards-section .all > section:nth-child(4).leave { transform: rotate(3deg) translateY(50em) scale3d(0.6, 0.6, 1); opacity: 0.3; }

.text-stacked-cards-section .all > section.active {
  transform: rotate(0);
  background-color: #fff;
}

.text-stacked-cards-section .all > section.active article {
  opacity: 1;
}
