css:
.test-text-section {
    padding: 4rem 0;
    background-color: var(--color-light, #FFFFFF);
    font-family: sans-serif;
}
.test-text-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.test-text-section .section-title {
    text-align: center;
    font-size: 2.5rem;
    margin-bottom: 3rem;
    color: var(--color-dark, #111827);
}
.test-text-section .grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: 2.5rem;
}
.test-text-section .column > * {
    margin-bottom: 1.5rem;
}
.test-text-section .column > *:last-child {
    margin-bottom: 0;
}
.test-text-section h1, .test-text-section h2, .test-text-section h3, .test-text-section h4, .test-text-section h5, .test-text-section h6 {
    font-weight: 700;
    color: var(--color-dark, #111827);
    line-height: 1.2;
}
.test-text-section h1 { font-size: 2.5rem; }
.test-text-section h2 { font-size: 2rem; }
.test-text-section h3 { font-size: 1.75rem; }
.test-text-section h4 { font-size: 1.5rem; }
.test-text-section h5 { font-size: 1.25rem; }
.test-text-section h6 { font-size: 1rem; }

.test-text-section p {
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
}
.test-text-section blockquote {
    border-left: 4px solid var(--color-primary, #667eea);
    padding-left: 1rem;
    font-style: italic;
    color: #4A5568;
    margin-left: 0;
}
.test-text-section ul, .test-text-section ol {
    padding-left: 1.5rem;
    color: var(--color-secondary, #6B7280);
}
.test-text-section li {
    margin-bottom: 0.5rem;
}
.test-text-section strong {
    font-weight: bold;
    color: var(--color-dark, #111827);
}
.test-text-section em {
    font-style: italic;
}
.test-text-section a {
    color: var(--color-primary, #667eea);
    text-decoration: underline;
}
.test-text-section code {
    background-color: #e5e7eb;
    padding: 0.2em 0.4em;
    border-radius: 4px;
    font-family: monospace;
    font-size: 0.9em;
}
.test-text-section .description {
    font-size: 0.8rem;
    color: #9CA3AF;
    margin-top: -1rem;
    border-top: 1px dashed #e5e7eb;
    padding-top: 0.5rem;
}
.test-text-section .anim-delay-1 { transition-delay: 0.1s; }
.test-text-section .anim-delay-2 { transition-delay: 0.2s; }
.test-text-section .anim-delay-3 { transition-delay: 0.3s; }
