css:
.timeline-bounce-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.timeline-bounce-section .container { width: 90%; max-width: 800px; margin: 0 auto; }
.timeline-bounce-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 4rem; color: var(--color-dark, #111827); }
.timeline { position: relative; }
.timeline::after {
    content: '';
    position: absolute;
    width: 3px;
    background-color: #e5e7eb;
    top: 0;
    bottom: 0;
    left: 50%;
    margin-left: -1.5px;
}
.timeline-item { padding: 10px 40px; position: relative; width: 50%; box-sizing: border-box; }
.timeline-item::after {
    content: '';
    position: absolute;
    width: 20px;
    height: 20px;
    right: -10px;
    top: 15px;
    background-color: var(--color-light, #fff);
    border: 4px solid var(--color-primary, #667eea);
    border-radius: 50%;
    z-index: 1;
    transform: scale(0);
}
.timeline-item.is-visible::after {
    animation: bounce-in 0.6s ease-out forwards;
    animation-delay: 0.3s;
}
@keyframes bounce-in {
    0% { transform: scale(0); }
    50% { transform: scale(1.2); }
    100% { transform: scale(1); }
}
.timeline-item:nth-child(odd) { left: 0; text-align: right; }
.timeline-item:nth-child(even) { left: 50%; }
.timeline-item:nth-child(even)::after { left: -10px; }
.timeline-content {
    padding: 20px 30px;
    background-color: #f9fafb;
    position: relative;
    border-radius: 8px;
    border: 1px solid #e5e7eb;
}
.timeline-content h3 { font-size: 1.25rem; margin-bottom: 0.5rem; color: var(--color-dark, #111827); }
.timeline-content p { color: var(--color-secondary, #6B7280); }
