css:
.timeline-card-deck-section { padding: 5rem 0; }
.timeline-card-deck-section .container { max-width: 600px; margin: 0 auto; }
.timeline-card-deck-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 2rem; color: var(--color-dark, #111827); }
.timeline-card-deck-section .card-deck {
    position: relative;
    height: 350px;
}
.timeline-card-deck-section .timeline-card {
    position: absolute;
    width: 100%;
    background-color: var(--color-light, #FFFFFF);
    border: 1px solid #e5e7eb;
    border-radius: 1rem;
    padding: 2rem;
    box-shadow: 0 10px 30px rgba(0,0,0,0.1);
    transition: transform 0.4s ease-out, box-shadow 0.4s ease-out;
    cursor: pointer;
}
.timeline-card-deck-section .timeline-card:nth-child(1) { transform: translateY(0) rotate(-2deg); z-index: 3; }
.timeline-card-deck-section .timeline-card:nth-child(2) { transform: translateY(20px) rotate(2deg); z-index: 2; }
.timeline-card-deck-section .timeline-card:nth-child(3) { transform: translateY(40px) rotate(-1deg); z-index: 1; }
.timeline-card-deck-section .card-deck:hover .timeline-card:nth-child(1) { transform: translateY(-120px) rotate(0); }
.timeline-card-deck-section .card-deck:hover .timeline-card:nth-child(2) { transform: translateY(0) rotate(0); }
.timeline-card-deck-section .card-deck:hover .timeline-card:nth-child(3) { transform: translateY(120px) rotate(0); }

.timeline-card-deck-section .card-year { font-weight: 600; color: var(--color-primary, #667eea); margin-bottom: 0.5rem; }
.timeline-card-deck-section h3 { font-size: 1.5rem; margin-bottom: 1rem; color: var(--color-dark, #111827); }
.timeline-card-deck-section p { color: var(--color-secondary, #6B7280); }
.timeline-card-deck-section .anim-delay-1 { animation-delay: 0.2s; }
.timeline-card-deck-section .anim-delay-2 { animation-delay: 0.4s; }
