css:
.timeline-centered-blocks-section {
    display: flex;
    flex-direction: column;
}
.timeline-centered-blocks-section .timeline-block {
    padding: 6rem 2rem;
    text-align: center;
    color: var(--color-dark, #1F2937);
}
.timeline-centered-blocks-section h2 {
    font-size: clamp(3rem, 8vw, 5rem);
    font-weight: 800;
    line-height: 1;
    opacity: 0.8;
}
.timeline-centered-blocks-section p {
    font-size: 1.25rem;
    font-weight: 500;
    margin-top: 1rem;
}
