css:
.timeline-dark-steps-section {
    padding: 5rem 0;
    background-color: var(--color-dark, #111827);
    color: var(--color-light, #FFFFFF);
}
.timeline-dark-steps-section .container {
    width: 90%;
    max-width: 1100px;
    margin: 0 auto;
}
.timeline-dark-steps-section .section-title {
    text-align: center;
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 3.5rem;
}
.timeline-dark-steps-section .steps-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2.5rem;
    position: relative;
}
.timeline-dark-steps-section .step-item {
    position: relative;
    padding-left: 3.5rem;
}
.timeline-dark-steps-section .step-number {
    position: absolute;
    left: 0;
    top: 0;
    font-size: 2rem;
    font-weight: 700;
    color: var(--color-primary, #4B88D7);
}
.timeline-dark-steps-section h3 {
    font-size: 1.25rem;
    font-weight: 600;
    margin-bottom: 0.75rem;
}
.timeline-dark-steps-section p {
    color: var(--color-secondary, #9CA3AF);
    line-height: 1.6;
}
.timeline-dark-steps-section .anim-delay-1 { transition-delay: 0.2s; }
.timeline-dark-steps-section .anim-delay-2 { transition-delay: 0.4s; }
