css:
.timeline-diagonal-section { padding: 5rem 0; overflow: hidden; }
.timeline-diagonal-section .container { max-width: 1000px; margin: 0 auto; }
.timeline-diagonal-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 4rem; }
.timeline-diagonal-section .timeline-wrapper { padding: 2rem 0; }
.timeline-diagonal-section .timeline {
    position: relative;
    margin: 0 auto;
    transform: rotate(-10deg);
}
.timeline-diagonal-section .timeline::before {
    content: '';
    position: absolute;
    top: 0; bottom: 0; left: 50%;
    width: 3px; 
    background-color: #e5e7eb;
    transform: translateX(-50%);
}
.timeline-diagonal-section .timeline-item { 
    position: relative; 
    width: 50%; 
    padding: 0 30px; 
    margin-bottom: 50px;
}
.timeline-diagonal-section .timeline-item:nth-child(odd) { left: 0; transform-origin: top right; }
.timeline-diagonal-section .timeline-item:nth-child(even) { left: 50%; transform-origin: top left; }
.timeline-diagonal-section .timeline-content {
    background-color: #f9fafb;
    padding: 1.5rem;
    border-radius: 8px;
    border: 1px solid #e5e7eb;
    transform: rotate(10deg); /* Counter-rotate */
}
.timeline-diagonal-section .timeline-item::after {
    content: '';
    position: absolute;
    width: 18px; height: 18px;
    border-radius: 50%;
    background-color: var(--color-primary, #667eea);
    border: 3px solid var(--color-light, #FFFFFF);
    top: 5px;
    z-index: 1;
}
.timeline-diagonal-section .timeline-item:nth-child(odd)::after { right: -9px; }
.timeline-diagonal-section .timeline-item:nth-child(even)::after { left: -9px; }
