css:
.timeline-horizontal-section {
    padding: 5rem 0;
    background-color: #f9fafb;
}
.timeline-horizontal-section .container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
}
.timeline-horizontal-section .section-title {
    text-align: center;
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 3rem;
    color: var(--color-dark, #1F2937);
}
.timeline-horizontal-section .timeline-wrapper {
    position: relative;
}
.timeline-horizontal-section .timeline-wrapper::before {
    content: '';
    position: absolute;
    top: 50%;
    left: 0;
    right: 0;
    height: 2px;
    background-color: #e5e7eb;
    transform: translateY(-50%);
}
.timeline-horizontal-section .timeline-scroll-container {
    display: flex;
    gap: 1.5rem;
    overflow-x: auto;
    padding: 2rem 0;
    scrollbar-width: thin; /* Firefox */
    scroll-snap-type: x mandatory;
    position: relative;
}
.timeline-horizontal-section .timeline-scroll-container::-webkit-scrollbar { height: 8px; }
.timeline-horizontal-section .timeline-scroll-container::-webkit-scrollbar-thumb { background: #d1d5db; border-radius: 4px; }

.timeline-horizontal-section .timeline-card {
    flex: 0 0 300px;
    background-color: var(--color-light, #FFFFFF);
    border-radius: 1rem;
    padding: 1.5rem;
    box-shadow: 0 4px 15px rgba(0,0,0,0.05);
    scroll-snap-align: center;
    position: relative;
    border-top: 4px solid var(--color-primary, #4B88D7);
}
.timeline-horizontal-section .timeline-card::after {
    content: '';
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 16px;
    height: 16px;
    background-color: var(--color-light, #FFFFFF);
    border: 3px solid var(--color-primary, #4B88D7);
    border-radius: 50%;
    z-index: -1;
}
.timeline-horizontal-section .timeline-year {
    position: absolute;
    top: calc(50% - 1.5rem - 16px);
    left: 50%;
    transform: translateX(-50%);
    background-color: var(--color-primary, #4B88D7);
    color: var(--color-light, #FFFFFF);
    padding: 0.25rem 0.75rem;
    border-radius: 9999px;
    font-size: 0.8rem;
    font-weight: 600;
}
.timeline-horizontal-section .timeline-card:nth-child(even) {
    margin-top: 3rem;
}
.timeline-horizontal-section .timeline-card:nth-child(odd) {
    margin-bottom: 3rem;
}
.timeline-horizontal-section .timeline-card h3 {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
}
.timeline-horizontal-section .timeline-card p {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
}
.timeline-horizontal-section .anim-delay-1 { transition-delay: 0.2s; }
