css:
.timeline-icon-steps-section { padding: 5rem 0; }
.timeline-icon-steps-section .container { max-width: 1000px; margin: 0 auto; padding: 0 1rem; }
.timeline-icon-steps-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 4rem; }
.timeline-icon-steps-section .steps-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1rem;
}
@media (min-width: 768px) {
    .timeline-icon-steps-section .steps-container {
        flex-direction: row;
        justify-content: center;
        align-items: flex-start;
        gap: 0;
    }
}
.timeline-icon-steps-section .step { text-align: center; max-width: 300px; }
.timeline-icon-steps-section .icon-wrapper {
    width: 80px;
    height: 80px;
    border-radius: 50%;
    background-color: #f3f4f6;
    color: var(--color-primary, #667eea);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 2rem;
    margin: 0 auto 1.5rem;
    border: 4px solid var(--color-light, #FFFFFF);
    box-shadow: 0 0 0 4px #e5e7eb;
}
.timeline-icon-steps-section h3 { font-size: 1.25rem; font-weight: 600; margin-bottom: 0.5rem; }
.timeline-icon-steps-section p { color: var(--color-secondary, #6B7280); }
.timeline-icon-steps-section .step-connector {
    height: 50px;
    width: 2px;
    background-color: #e5e7eb;
}
@media (min-width: 768px) {
    .timeline-icon-steps-section .step-connector {
        height: 2px;
        width: 100%;
        flex-grow: 1;
        margin: 40px -1rem 0;
    }
}
.timeline-icon-steps-section .anim-delay-1 { animation-delay: 0.2s; }
.timeline-icon-steps-section .anim-delay-2 { animation-delay: 0.4s; }
