css:
.timeline-minimal-section {
    padding: 5rem 0;
    background-color: var(--color-light, #FFFFFF);
}
.timeline-minimal-section .container {
    width: 90%;
    max-width: 800px;
    margin: 0 auto;
}
.timeline-minimal-section .section-title {
    text-align: center;
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 3rem;
    color: var(--color-dark, #1F2937);
}
.timeline-minimal-section .timeline-list {
    position: relative;
    padding-left: 2rem;
    border-left: 2px solid #e5e7eb; /* gray-200 */
}
.timeline-minimal-section .timeline-item {
    position: relative;
    margin-bottom: 2.5rem;
}
.timeline-minimal-section .timeline-item:last-child {
    margin-bottom: 0;
}
.timeline-minimal-section .timeline-item::before {
    content: '';
    position: absolute;
    left: -2.5rem;
    top: 5px;
    width: 16px;
    height: 16px;
    border-radius: 50%;
    background-color: var(--color-light, #FFFFFF);
    border: 3px solid var(--color-primary, #4B88D7);
}
.timeline-minimal-section .timeline-date {
    font-size: 0.9rem;
    font-weight: 600;
    color: var(--color-secondary, #6B7280);
    margin-bottom: 0.5rem;
}
.timeline-minimal-section h3 {
    font-size: 1.25rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: var(--color-dark, #1F2937);
}
.timeline-minimal-section p {
    font-size: 1rem;
    color: var(--color-secondary, #6B7280);
    line-height: 1.6;
}
.timeline-minimal-section .anim-delay-1 { transition-delay: 0.2s; }
.timeline-minimal-section .anim-delay-2 { transition-delay: 0.4s; }
