css:
.timeline-photo-wall-section {
    padding: 5rem 0;
    background-color: #f3f4f6;
}
.timeline-photo-wall-section .container {
    width: 90%;
    max-width: 1000px;
    margin: 0 auto;
}
.timeline-photo-wall-section .section-title {
    text-align: center;
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 3rem;
    color: var(--color-dark, #1F2937);
}
.timeline-photo-wall-section .timeline-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
}
.timeline-photo-wall-section .timeline-item {
    display: contents; /* Allows direct grid placement of children */
}
.timeline-photo-wall-section .image-card {
    position: relative;
    border-radius: 1rem;
    overflow: hidden;
    aspect-ratio: 16/9;
    color: var(--color-light, #FFFFFF);
}
.timeline-photo-wall-section .image-card img {
    width: 100%;
    height: 100%;
    object-fit: cover;
}
.timeline-photo-wall-section .card-overlay {
    position: absolute;
    inset: 0;
    background: linear-gradient(to right, rgba(0,0,0,0.7) 0%, transparent 100%);
    padding: 2rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
}
.timeline-photo-wall-section .timeline-item:nth-child(even) .card-overlay {
    background: linear-gradient(to left, rgba(0,0,0,0.7) 0%, transparent 100%);
    text-align: right;
    align-items: flex-end;
}
.timeline-photo-wall-section .timeline-date {
    font-weight: 600;
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
    opacity: 0.8;
}
.timeline-photo-wall-section h3 {
    font-size: 1.75rem;
    margin-bottom: 1rem;
    font-weight: 700;
}
.timeline-photo-wall-section p {
    max-width: 350px;
    line-height: 1.6;
}
