css:
/* الحاوية الرئيسية - بدون خلفية */
.timeline-section {
    padding: 5rem 0;
    background: transparent;
    position: relative;
    overflow: hidden;
}

.timeline-section .container {
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1.5rem;
    position: relative;
    z-index: 1;
}

/* رأس القسم */
.timeline-header {
    text-align: center;
    margin-bottom: 5rem;
}

.timeline-section .section-title {
    font-size: 3rem;
    font-weight: 800;
    background: linear-gradient(135deg, #A67C52 0%, #8B6F47 50%, #6D563C 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin-bottom: 1rem;
    letter-spacing: -0.02em;
}

.timeline-section .section-subtitle {
    font-size: 1.125rem;
    color: #8B7355;
    font-weight: 500;
}

/* خط الزمن */
.timeline {
    position: relative;
    max-width: 900px;
    margin: 0 auto;
    padding: 2rem 0;
}

/* الخط العمودي المركزي */
.timeline::before {
    content: '';
    position: absolute;
    width: 3px;
    background: linear-gradient(180deg, 
        transparent 0%, 
        #D4AF85 5%, 
        #B5926D 50%, 
        #A67C52 95%, 
        transparent 100%
    );
    top: 0;
    bottom: 0;
    left: 50%;
    transform: translateX(-50%);
    box-shadow: 0 0 20px rgba(166, 124, 82, 0.3);
}

/* عناصر الخط الزمني */
.timeline-item {
    padding: 0 0 4rem 0;
    position: relative;
    width: 100%;
    display: flex;
    justify-content: flex-start;
    opacity: 0;
    transform: translateY(30px);
}

/* العناصر الفردية - اليسار */
.timeline-item:nth-child(odd) {
    justify-content: flex-end;
}

.timeline-item:nth-child(odd) .timeline-content {
    margin-right: calc(50% + 50px);
    text-align: right;
}

/* العناصر الزوجية - اليمين */
.timeline-item:nth-child(even) .timeline-content {
    margin-left: calc(50% + 50px);
    text-align: left;
}

/* الدوائر على الخط */
.timeline-item::before {
    content: '';
    position: absolute;
    width: 24px;
    height: 24px;
    background: linear-gradient(135deg, #F5E6D3 0%, #E8D4B8 100%);
    border: 4px solid #A67C52;
    border-radius: 50%;
    top: 30px;
    left: 50%;
    transform: translateX(-50%);
    z-index: 3;
    box-shadow: 0 0 0 8px rgba(255, 255, 255, 0.3),
                0 4px 12px rgba(166, 124, 82, 0.3);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
}

.timeline-item:hover::before {
    transform: translateX(-50%) scale(1.3);
    background: linear-gradient(135deg, #D4AF85 0%, #B5926D 100%);
    box-shadow: 0 0 0 12px rgba(212, 175, 133, 0.3),
                0 6px 20px rgba(166, 124, 82, 0.5);
}

/* محتوى الخط الزمني - بعرض ثابت */
.timeline-content {
    background: linear-gradient(135deg, #FFFFFF 0%, #FAF8F3 100%);
    padding: 2rem 2.5rem;
    border-radius: 20px;
    box-shadow: 0 10px 40px rgba(139, 115, 85, 0.15),
                0 2px 8px rgba(139, 115, 85, 0.1);
    position: relative;
    width: 420px;
    min-width: 420px;
    max-width: 420px;
    height: auto;
    border: 1px solid rgba(212, 175, 133, 0.2);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    overflow: visible;
}

.timeline-content::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #D4AF85 0%, #B5926D 50%, #A67C52 100%);
    transform: scaleX(0);
    transform-origin: left;
    transition: transform 0.6s cubic-bezier(0.4, 0, 0.2, 1);
}

.timeline-item:hover .timeline-content::before {
    transform: scaleX(1);
}

.timeline-content:hover {
    transform: translateY(-8px);
    box-shadow: 0 20px 60px rgba(139, 115, 85, 0.25),
                0 4px 16px rgba(139, 115, 85, 0.15);
    border-color: rgba(166, 124, 82, 0.4);
}

/* الأيقونة الحيادية الثابتة */
.timeline-icon {
    width: 56px;
    height: 56px;
    background: linear-gradient(135deg, #D4AF85 0%, #B5926D 100%);
    border-radius: 16px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin-bottom: 1.5rem;
    box-shadow: 0 8px 24px rgba(166, 124, 82, 0.25);
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    flex-shrink: 0;
}

.timeline-icon svg {
    width: 20px;
    height: 20px;
    color: #FFFFFF;
    transition: none;
}

.timeline-content:hover .timeline-icon {
    background: linear-gradient(135deg, #B5926D 0%, #A67C52 100%);
    transform: scale(1.05);
}

/* السنة */
.timeline-year {
    display: inline-block;
    font-size: 0.875rem;
    font-weight: 700;
    color: #A67C52;
    background: linear-gradient(135deg, rgba(212, 175, 133, 0.2) 0%, rgba(181, 146, 109, 0.15) 100%);
    padding: 0.5rem 1.25rem;
    border-radius: 50px;
    margin-bottom: 1rem;
    letter-spacing: 0.05em;
    border: 1px solid rgba(166, 124, 82, 0.2);
    flex-shrink: 0;
}

/* العنوان - مع التفاف النص */
.timeline-content h3 {
    font-size: 1.5rem;
    font-weight: 700;
    color: #4A3F35;
    margin-bottom: 1rem;
    line-height: 1.3;
    transition: color 0.3s ease;
    word-wrap: break-word;
    overflow-wrap: break-word;
    hyphens: auto;
}

.timeline-content:hover h3 {
    color: #6D563C;
}

/* الفقرة - مع التفاف النص */
.timeline-content p {
    font-size: 1rem;
    color: #6B5D52;
    line-height: 1.7;
    margin: 0;
    word-wrap: break-word;
    overflow-wrap: break-word;
    hyphens: auto;
}

/* الأنيميشن */
@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(40px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.timeline-item.anim-fade-in-up {
    animation: fadeInUp 0.8s cubic-bezier(0.4, 0, 0.2, 1) forwards;
}

.timeline-item:nth-child(1) { animation-delay: 0.1s; }
.timeline-item:nth-child(2) { animation-delay: 0.3s; }
.timeline-item:nth-child(3) { animation-delay: 0.5s; }
.timeline-item:nth-child(4) { animation-delay: 0.7s; }

/* التصميم المتجاوب */
@media (max-width: 768px) {
    .timeline-section {
        padding: 3rem 0;
    }

    .timeline-header {
        margin-bottom: 3rem;
    }

    .timeline-section .section-title {
        font-size: 2rem;
    }

    .timeline-section .section-subtitle {
        font-size: 1rem;
    }

    .timeline::before {
        left: 30px;
        transform: none;
    }

    .timeline-item {
        padding: 0 0 3rem 0;
    }

    .timeline-item:nth-child(odd) .timeline-content,
    .timeline-item:nth-child(even) .timeline-content {
        margin-left: 70px;
        margin-right: 0;
        text-align: left;
        width: calc(100% - 70px);
        min-width: auto;
        max-width: calc(100% - 70px);
    }

    .timeline-item::before {
        left: 30px;
        transform: translateX(-50%);
        top: 25px;
        width: 20px;
        height: 20px;
    }

    .timeline-content {
        padding: 1.5rem 1.75rem;
    }

    .timeline-icon {
        width: 48px;
        height: 48px;
    }

    .timeline-icon svg {
        width: 18px;
        height: 18px;
    }

    .timeline-content h3 {
        font-size: 1.25rem;
    }

    .timeline-content p {
        font-size: 0.9375rem;
    }
}

@media (max-width: 480px) {
    .timeline-section .container {
        padding: 0 1rem;
    }

    .timeline-section .section-title {
        font-size: 1.75rem;
    }

    .timeline-content {
        padding: 1.25rem 1.5rem;
    }

    .timeline-icon {
        width: 44px;
        height: 44px;
    }
}
