/* Timeline Section - Elegant White to Beige Design */
.timeline-wavy-section {
    padding: 5rem 0;
    background: linear-gradient(180deg, #FFFFFF 0%, #FBF5EA 50%, #F8EDE0 100%);
    position: relative;
    overflow: hidden;
}

.timeline-wavy-section::before {
    content: '';
    position: absolute;
    top: -50%;
    left: -10%;
    width: 120%;
    height: 200%;
    background: radial-gradient(circle at 50% 50%, rgba(248, 237, 224, 0.3) 0%, transparent 70%);
    pointer-events: none;
    animation: float 20s ease-in-out infinite;
}

@keyframes float {
    0%, 100% { transform: translate(0, 0) scale(1); }
    50% { transform: translate(20px, 30px) scale(1.05); }
}

.timeline-wavy-section .container {
    max-width: 900px;
    margin: 0 auto;
    padding: 0 2rem;
    position: relative;
    z-index: 1;
}

.timeline-wavy-section .section-title {
    text-align: center;
    font-size: 2.8rem;
    margin-bottom: 4rem;
    color: #5D4E37;
    font-weight: 700;
    opacity: 0;
    animation: fadeInDown 1s ease forwards;
    letter-spacing: -0.5px;
}

@keyframes fadeInDown {
    from {
        opacity: 0;
        transform: translateY(-30px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

/* Timeline Container */
.timeline-wavy-section .timeline {
    position: relative;
    padding: 2rem 0;
}

/* Animated Timeline Line */
.timeline-wavy-section .timeline-line {
    position: absolute;
    left: 50%;
    top: 0;
    bottom: 0;
    width: 3px;
    background: linear-gradient(180deg, rgba(206, 184, 157, 0.3) 0%, #CEB89D 50%, rgba(206, 184, 157, 0.3) 100%);
    transform: translateX(-50%);
    overflow: hidden;
}

.timeline-wavy-section .timeline-line::before {
    content: '';
    position: absolute;
    top: -100%;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(180deg, transparent 0%, #A67C5B 50%, transparent 100%);
    animation: lineFlow 3s ease-in-out infinite;
}

@keyframes lineFlow {
    0% { top: -100%; }
    100% { top: 200%; }
}

/* Timeline Items */
.timeline-wavy-section .timeline-item {
    position: relative;
    margin-bottom: 4rem;
    opacity: 0;
    animation: fadeInUp 0.8s ease forwards;
}

@keyframes fadeInUp {
    from {
        opacity: 0;
        transform: translateY(40px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.timeline-wavy-section .timeline-item:nth-child(2) {
    display: flex;
    justify-content: flex-start;
    padding-right: calc(50% + 3rem);
}

.timeline-wavy-section .timeline-item:nth-child(3) {
    display: flex;
    justify-content: flex-end;
    padding-left: calc(50% + 3rem);
}

.timeline-wavy-section .timeline-item:nth-child(4) {
    display: flex;
    justify-content: flex-start;
    padding-right: calc(50% + 3rem);
}

/* Timeline Dot with Pulse Effect */
.timeline-wavy-section .timeline-dot {
    position: absolute;
    left: 50%;
    top: 1.5rem;
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background: linear-gradient(135deg, #A67C5B 0%, #C9AD7F 100%);
    transform: translateX(-50%);
    z-index: 2;
    box-shadow: 0 0 0 0 rgba(166, 124, 91, 0.4);
    animation: pulse 2s infinite;
}

@keyframes pulse {
    0% {
        box-shadow: 0 0 0 0 rgba(166, 124, 91, 0.7);
    }
    70% {
        box-shadow: 0 0 0 15px rgba(166, 124, 91, 0);
    }
    100% {
        box-shadow: 0 0 0 0 rgba(166, 124, 91, 0);
    }
}

.timeline-wavy-section .dot-pulse {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: #FFFFFF;
    animation: dotPulse 2s ease-in-out infinite;
}

@keyframes dotPulse {
    0%, 100% {
        transform: translate(-50%, -50%) scale(1);
        opacity: 1;
    }
    50% {
        transform: translate(-50%, -50%) scale(1.5);
        opacity: 0.7;
    }
}

/* Timeline Content Card */
.timeline-wavy-section .timeline-content {
    background: linear-gradient(135deg, rgba(255, 255, 255, 0.95) 0%, rgba(251, 245, 234, 0.9) 100%);
    padding: 2rem;
    border-radius: 16px;
    box-shadow: 0 8px 32px rgba(93, 78, 55, 0.08), 0 2px 8px rgba(93, 78, 55, 0.04);
    backdrop-filter: blur(10px);
    border: 1px solid rgba(255, 255, 255, 0.7);
    position: relative;
    transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
    max-width: 380px;
}

.timeline-wavy-section .timeline-content:hover {
    transform: translateY(-8px) scale(1.02);
    box-shadow: 0 16px 48px rgba(93, 78, 55, 0.12), 0 4px 12px rgba(93, 78, 55, 0.06);
}

.timeline-wavy-section .timeline-content::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    height: 4px;
    background: linear-gradient(90deg, #A67C5B 0%, #C9AD7F 50%, #DECFAC 100%);
    border-radius: 16px 16px 0 0;
    opacity: 0;
    transition: opacity 0.3s ease;
}

.timeline-wavy-section .timeline-content:hover::before {
    opacity: 1;
}

/* Timeline Year Badge */
.timeline-wavy-section .timeline-year {
    display: inline-block;
    background: linear-gradient(135deg, #EDE8D0 0%, #DECFAC 100%);
    color: #5D4E37;
    padding: 0.4rem 1rem;
    border-radius: 20px;
    font-size: 0.85rem;
    font-weight: 600;
    margin-bottom: 1rem;
    letter-spacing: 0.5px;
    box-shadow: 0 2px 8px rgba(93, 78, 55, 0.1);
}

.timeline-wavy-section h3 {
    font-size: 1.5rem;
    font-weight: 700;
    color: #5D4E37;
    margin-bottom: 0.75rem;
    line-height: 1.4;
}

.timeline-wavy-section p {
    color: #8B7355;
    line-height: 1.7;
    font-size: 1rem;
    margin: 0;
}

/* Responsive Design */
@media (max-width: 768px) {
    .timeline-wavy-section {
        padding: 3rem 0;
    }

    .timeline-wavy-section .section-title {
        font-size: 2rem;
        margin-bottom: 3rem;
    }

    .timeline-wavy-section .timeline-line {
        left: 1.5rem;
    }

    .timeline-wavy-section .timeline-dot {
        left: 1.5rem;
    }

    .timeline-wavy-section .timeline-item:nth-child(2),
    .timeline-wavy-section .timeline-item:nth-child(3),
    .timeline-wavy-section .timeline-item:nth-child(4) {
        padding-left: 4rem;
        padding-right: 0;
        justify-content: flex-start;
    }

    .timeline-wavy-section .timeline-content {
        max-width: 100%;
    }
}

/* Scroll Animation Support */
@supports (animation-timeline: view()) {
    .timeline-wavy-section .timeline-item {
        animation: fadeInUp 1s linear;
        animation-timeline: view();
        animation-range: entry 0% cover 30%;
    }
}
