css:
.case-study-teaser-section { padding: 4rem 0; background-color: var(--color-surface-2, #2d2d2d); position: relative; }
.case-study-teaser-section .container { max-width: 900px; margin: 0 auto; width: 90%; display: flex; justify-content: center; }
.case-study-teaser-section .content { text-align: center; color: var(--color-light, #FFFFFF); max-width: 600px; }
.case-study-teaser-section .client-logo { height: 40px; margin: 0 auto 1.5rem; filter: brightness(0) invert(1); }
.case-study-teaser-section h2 { font-size: 2rem; font-weight: 600; margin-bottom: 1.5rem; }
.case-study-teaser-section p { color: var(--color-secondary, #a0a0a0); margin-bottom: 2rem; }
.case-study-teaser-section .btn-primary { background-color: var(--color-primary, #FF6A00); color: var(--color-dark, #000000); padding: 0.8rem 2rem; border-radius: 8px; text-decoration: none; font-weight: 600; }
