css:
.trust-checklist-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); position: relative; }
.trust-checklist-section .container { display: grid; grid-template-columns: 1fr; gap: 2rem; align-items: center; max-width: 1000px; margin: 0 auto; width: 90%; }
@media (min-width: 768px) { .trust-checklist-section .container { grid-template-columns: 1fr 1fr; gap: 4rem; } .trust-checklist-section .image-side { order: 1; } }
.trust-checklist-section .image-side img { border-radius: 12px; width: 100%; height: auto; object-fit: cover; }
.trust-checklist-section h2 { font-size: 2.5rem; margin-bottom: 2rem; color: var(--color-dark, #111827); }
.trust-checklist-section ul { list-style: none; padding: 0; }
.trust-checklist-section li { margin-bottom: 1rem; font-size: 1.1rem; color: var(--color-secondary, #6B7280); }
.trust-checklist-section li span { color: var(--color-primary, #667eea); font-weight: bold; margin-right: 0.5rem; }
