css:
.data-snapshot-section { padding: 4rem 0; background-color: #f3f4f6; position: relative; }
.data-snapshot-section .container { text-align: center; max-width: 1000px; margin: 0 auto; width: 90%; }
.data-snapshot-section .section-title { font-size: 2rem; margin-bottom: 3rem; color: var(--color-dark, #111827); }
.data-snapshot-section .stats-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(150px, 1fr)); gap: 2rem; }
.data-snapshot-section .stat-number { font-size: 3.5rem; font-weight: 700; color: var(--color-primary, #4B88D7); display: block; line-height: 1; }
.data-snapshot-section .stat-item p { color: var(--color-secondary, #6B7280); margin-top: 0.5rem; font-weight: 500; }
