css:
.featured-in-section { padding: 3rem 0; background-color: transparent; position: relative; }
.featured-in-section .container { width: 90%; max-width: 1000px; margin: 0 auto; }
.featured-in-section .section-title { font-size: 1rem; font-weight: 500; color: var(--color-secondary, #6B7280); text-transform: uppercase; letter-spacing: 1px; margin-bottom: 2rem; }
.featured-in-section .logo-grid { display: flex; flex-wrap: wrap; justify-content: center; align-items: center; gap: 3rem; }
.featured-in-section .logo-grid img { height: 28px; width: auto; filter: grayscale(100%) opacity(0.6); transition: filter 0.3s, opacity 0.3s; }
.featured-in-section .logo-grid img:hover { filter: grayscale(0%) opacity(1); }
