css:
.guarantee-banner-section { background-color: var(--color-surface-2, #2d2d2d); padding: 4rem 0; position: relative; }
.guarantee-banner-section .container { display: grid; grid-template-columns: repeat(auto-fit, minmax(250px, 1fr)); gap: 2rem; max-width: 1000px; margin: 0 auto; width: 90%; }
.guarantee-banner-section .guarantee-item { text-align: center; color: var(--color-light, #FFFFFF); }
.guarantee-banner-section .icon { font-size: 2.5rem; margin-bottom: 1rem; }
.guarantee-banner-section h3 { font-size: 1.2rem; font-weight: 600; margin-bottom: 0.5rem; }
.guarantee-banner-section p { font-size: 0.9rem; color: var(--color-secondary, #a0a0a0); }
