css:
.logo-wall-section { padding: 3rem 0; background-color: transparent; position: relative; }
.logo-wall-section .container { width: 90%; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.logo-wall-section .text-center { text-align: center; }
.logo-wall-section .logo-wall-title { font-size: 1rem; font-weight: 500; color: var(--color-secondary, #6B7280); text-transform: uppercase; letter-spacing: 1px; margin-bottom: 2rem; }
.logo-wall-section .logo-grid { display: flex; flex-wrap: wrap; justify-content: center; align-items: center; gap: 3rem; }
.logo-wall-section .logo-grid img { height: 32px; width: auto; filter: grayscale(100%) opacity(0.6); transition: filter 0.3s; }
.logo-wall-section .logo-grid img:hover { filter: grayscale(0%) opacity(1); }
