css:
.review-snippets-section { padding: 4rem 0; background-color: #f9fafb; position: relative; }
.review-snippets-section .container { width: 90%; max-width: 1100px; margin: 0 auto; }
.review-snippets-section .section-title { text-align: center; font-size: 2rem; margin-bottom: 3rem; color: var(--color-dark, #111827); }
.review-snippets-section .reviews-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 1.5rem; }
.review-snippets-section .review-card { background-color: var(--color-light, #FFFFFF); border: 1px solid #e5e7eb; padding: 1.5rem; border-radius: 8px; }
.review-snippets-section .stars { color: #f59e0b; margin-bottom: 0.75rem; }
.review-snippets-section .quote { font-style: italic; margin-bottom: 1rem; color: var(--color-secondary, #6B7280); }
.review-snippets-section .author { text-align: right; font-weight: 600; color: var(--color-dark, #111827); }
