css:
.single-testimonial-section { padding: 5rem 0; background-color: var(--color-dark, #111827); color: var(--color-light, #FFFFFF); position: relative; }
.single-testimonial-section .container { max-width: 800px; margin: 0 auto; text-align: center; }
.single-testimonial-section .testimonial-content img { width: 100px; height: 100px; border-radius: 50%; object-fit: cover; margin: 0 auto 1.5rem; border: 3px solid var(--color-primary, #4B88D7); }
.single-testimonial-section blockquote { font-size: 1.5rem; font-weight: 500; line-height: 1.6; margin-bottom: 2rem; }
.single-testimonial-section cite { font-style: normal; font-weight: 600; color: var(--color-secondary, #9CA3AF); }
