css:
.testimonial-grid-section { padding: 5rem 0; background-color: #f9fafb; position: relative; }
.testimonial-grid-section .container { width: 90%; max-width: 1100px; margin: 0 auto; }
.testimonial-grid-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 3rem; color: var(--color-dark, #111827); }
.testimonial-grid-section .testimonial-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.testimonial-grid-section .testimonial-card { background-color: var(--color-light, #FFFFFF); border-radius: 1rem; padding: 2rem; box-shadow: 0 4px 20px rgba(0,0,0,0.05); }
.testimonial-grid-section .quote { font-style: italic; color: var(--color-secondary, #6B7280); border-left: 3px solid var(--color-primary, #4B88D7); padding-left: 1rem; margin-bottom: 1.5rem; }
.testimonial-grid-section .author { display: flex; align-items: center; gap: 1rem; }
.testimonial-grid-section .author img { width: 50px; height: 50px; border-radius: 50%; object-fit: cover; }
.testimonial-grid-section .author h4 { font-weight: 600; margin: 0; }
.testimonial-grid-section .author span { font-size: 0.9rem; color: var(--color-secondary, #6B7280); }
