css:
.video-testimonial-section { padding: 4rem 0; position: relative; }
.video-testimonial-section .container { max-width: 900px; margin: 0 auto; width: 90%; text-align: center; }
.video-testimonial-section .section-title { font-size: 2rem; margin-bottom: 3rem; color: var(--color-dark, #111827); }
.video-testimonial-section .content-grid { display: grid; grid-template-columns: 1fr; gap: 2rem; align-items: center; text-align: left; }
@media(min-width: 768px) { .video-testimonial-section .content-grid { grid-template-columns: 1fr 1fr; } }
.video-testimonial-section .video-wrapper { position: relative; border-radius: 1rem; overflow: hidden; cursor: pointer; }
.video-testimonial-section .video-wrapper img { width: 100%; display: block; }
.video-testimonial-section .play-button { position: absolute; top: 50%; left: 50%; transform: translate(-50%, -50%); width: 60px; height: 60px; background: rgba(255,255,255,0.8); border-radius: 50%; display: flex; align-items: center; justify-content: center; font-size: 1.5rem; color: var(--color-dark, #111827); transition: transform 0.3s; }
.video-testimonial-section .video-wrapper:hover .play-button { transform: translate(-50%, -50%) scale(1.1); }
.video-testimonial-section blockquote { font-size: 1.2rem; font-style: italic; border-left: 3px solid var(--color-primary, #4B88D7); padding-left: 1rem; margin-bottom: 1rem; }
.video-testimonial-section cite { font-style: normal; font-weight: 600; color: var(--color-secondary, #6B7280); }
