css:
.hero-sport-motivational-quote {
        position: relative;
        display: flex;
        align-items: center;
        justify-content: center;
        text-align: center;
        min-height: 70vh;
        color: #ffffff;
        overflow: hidden;
        background-color: #34495e;
    }
    .hero-sport-motivational-quote .quote-background img {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        object-fit: cover;
        z-index: 1;
        filter: brightness(0.4) contrast(1.1);
    }
    .hero-sport-motivational-quote .quote-overlay {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        background-color: rgba(0, 0, 0, 0.5);
        z-index: 2;
    }
    .hero-sport-motivational-quote .quote-content {
        position: relative;
        z-index: 3;
        padding: 20px;
        max-width: 700px;
    }
    .hero-sport-motivational-quote q {
        font-size: 2rem;
        font-style: italic;
        font-weight: 500;
        margin-bottom: 15px;
        display: block;
        line-height: 1.5;
        color: #f1c40f;
    }
    .hero-sport-motivational-quote p {
        font-size: 1.1rem;
        margin-bottom: 30px;
        font-weight: 600;
        color: #ecf0f1;
    }
    .hero-sport-motivational-quote .btn-start-journey {
        display: inline-block;
        padding: 12px 28px;
        background-color: #f1c40f;
        color: #34495e;
        text-decoration: none;
        font-weight: 700;
        border-radius: 5px;
        transition: background-color 0.3s ease, color 0.3s ease;
    }
    .hero-sport-motivational-quote .btn-start-journey:hover {
        background-color: #f39c12;
        color: #ffffff;
    }
    @media (min-width: 768px) {
        .hero-sport-motivational-quote {
            min-height: 85vh;
        }
        .hero-sport-motivational-quote q {
            font-size: 3.5rem;
        }
        .hero-sport-motivational-quote p {
            font-size: 1.4rem;
        }
    }

js:
window.addEventListener('load', () => {
        document.querySelectorAll('img[src^="https://pixabay.com/api/"]').forEach(img => {
            const url = img.getAttribute('src');
            fetch(url)
                .then(response => response.json())
                .then(data => {
                    if (data.hits && data.hits.length > 0) {
                        img.setAttribute('src', data.hits[0].largeImageURL);
                    } else {
                        img.setAttribute('src', 'https://via.placeholder.com/1280x720?text=Image+Not+Found');
                    }
                })
                .catch(() => {
                    img.setAttribute('src', 'https://via.placeholder.com/1280x720?text=Image+Not+Found');
                });
        });
    });
