css:
.hero-sport-tech-innovation {
        display: flex;
        flex-direction: column;
        align-items: center;
        justify-content: center;
        padding: 40px 20px;
        min-height: 70vh;
        background-color: #000000;
        color: #ffffff;
        overflow: hidden;
    }
    .hero-sport-tech-innovation .hero-content {
        text-align: center;
        margin-bottom: 30px;
        max-width: 700px;
    }
    .hero-sport-tech-innovation h3 {
        font-size: 1.2rem;
        font-weight: 400;
        color: #00f2ff;
        letter-spacing: 1px;
        margin-bottom: 10px;
    }
    .hero-sport-tech-innovation h1 {
        font-size: 2.5rem;
        margin-bottom: 15px;
        font-weight: 800;
        letter-spacing: 1.2px;
    }
    .hero-sport-tech-innovation p {
        font-size: 1rem;
        line-height: 1.6;
        color: #cccccc;
    }
    .hero-sport-tech-innovation .tech-btn {
        display: inline-block;
        margin-top: 30px;
        padding: 12px 28px;
        background-color: #00f2ff;
        color: #000000;
        text-decoration: none;
        font-weight: 700;
        border-radius: 5px;
        transition: background-color 0.3s ease, color 0.3s ease;
    }
    .hero-sport-tech-innovation .tech-btn:hover {
        background-color: #00d4d4;
    }
    .hero-sport-tech-innovation .hero-image {
        width: 100%;
        max-width: 800px;
    }
    .hero-sport-tech-innovation img {
        display: block;
        width: 100%;
        height: auto;
        border-radius: 10px;
    }
    @media (min-width: 768px) {
        .hero-sport-tech-innovation {
            flex-direction: row;
            justify-content: space-between;
            padding: 80px 5%;
            min-height: 80vh;
        }
        .hero-sport-tech-innovation .hero-content {
            flex-basis: 50%;
            text-align: left;
            margin-bottom: 0;
        }
        .hero-sport-tech-innovation h1 {
            font-size: 3.8rem;
        }
        .hero-sport-tech-innovation h3 {
             font-size: 1.5rem;
        }
        .hero-sport-tech-innovation p {
            font-size: 1.2rem;
            max-width: 450px;
        }
        .hero-sport-tech-innovation .hero-image {
            flex-basis: 45%;
            max-width: 45%;
        }
    }

js:
window.addEventListener('load', () => {
        document.querySelectorAll('img[src^="https://pixabay.com/api/"]').forEach(img => {
            const url = img.getAttribute('src');
            fetch(url)
                .then(response => response.json())
                .then(data => {
                    if (data.hits && data.hits.length > 0) {
                        img.setAttribute('src', data.hits[0].largeImageURL);
                    } else {
                        img.setAttribute('src', 'https://via.placeholder.com/1280x720?text=Image+Not+Found');
                    }
                })
                .catch(() => {
                    img.setAttribute('src', 'https://via.placeholder.com/1280x720?text=Image+Not+Found');
                });
        });
    });
